INSERT INTO `tb_article` VALUES (1, 1, 'SpringMVCInterceptor ', '', '<p>SpringMVC Interceptor 12306 </p>\n<p>&nbsp;</p>\n<p>Interceptor</p>\n<p>SpringMVC Interceptor HandlerInterceptor SpringMVC Interceptor InterceptorSpring HandlerInterceptor HandlerInterceptor Spring HandlerInterceptor HandlerInterceptorAdapter SpringWebRequestInterceptorWebRequestInterceptor</p>\n<p>&nbsp;</p>\n<p>HandlerInterceptor</p>\n<p>HandlerInterceptor </p>\n<p>&nbsp;</p>\n<p>1 preHandle (HttpServletRequest request, HttpServletResponse response, Object handle) SpringMVC Interceptor Interceptor Interceptor Interceptor preHandle Boolean false Interceptor Controller true Interceptor preHandle Interceptor Controller </p>\n<p>&nbsp;</p>\n<p>2 postHandle (HttpServletRequest request, HttpServletResponse response, Object handle, ModelAndView modelAndView) preHandle afterCompletion Interceptor preHandle true postHandle Controller DispatcherServlet Controller ModelAndView postHandle preHandle Interceptor postHandle Struts2Interceptor Struts2 Interceptor Struts2 ActionInvocation invoke Interceptor Action Interceptor invoke invoke </p>\n<p>&nbsp;</p>\n<p>3 afterCompletion(HttpServletRequest request, HttpServletResponse response, Object handle, Exception ex) Interceptor preHandle true DispatcherServlet </p>\n<p>&nbsp;</p>\n<p></p>\n<p>&nbsp;</p>\n<p>import javax.servlet.http.HttpServletRequest;</p>\n<p>import javax.servlet.http.HttpServletResponse;</p>\n<p>import org.springframework.web.servlet.HandlerInterceptor;</p>\n<p>import org.springframework.web.servlet.ModelAndView;</p>\n<p>public class SpringMVCInterceptor implements HandlerInterceptor {</p>\n<p>&nbsp; &nbsp; /**&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* preHandleControllerSpringMVCInterceptor&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* InterceptorSpringMVCInterceptorpreHandle&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* ControllerSpringMVCInterceptorpreHandle&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* falsepreHandlefalse&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;*/</p>\n<p>&nbsp; &nbsp; @Override</p>\n<p>&nbsp; &nbsp; public boolean preHandle(HttpServletRequest request,</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; HttpServletResponse response, Object handler) throws Exception {</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; // TODO Auto-generated method stub&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; return false;</p>\n<p>&nbsp; &nbsp; }</p>\n<p>&nbsp; &nbsp; /**&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* InterceptorpreHandletruepostHandle&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* ControllerDispatcherServletModelAndView&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* InterceptorStruts2&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* Struts2interceptActionInvocationinvokeStruts2ActionInvocationinvokeInterceptor&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* actionInterceptorinvokeInterceptorinvoke&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;*/</p>\n<p>&nbsp; &nbsp; @Override</p>\n<p>&nbsp; &nbsp; public void postHandle(HttpServletRequest request,</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; HttpServletResponse response, Object handler,</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ModelAndView modelAndView) throws Exception {</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; // TODO Auto-generated method stub&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; }</p>\n<p>&nbsp; &nbsp; /**&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* InterceptorpreHandletrueDispatcherServlet&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* InterceptorpreHandletrue&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;*/</p>\n<p>&nbsp; &nbsp; @Override</p>\n<p>&nbsp; &nbsp; public void afterCompletion(HttpServletRequest request,</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; HttpServletResponse response, Object handler, Exception ex)</p>\n<p>&nbsp; &nbsp; throws Exception {</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; // TODO Auto-generated method stub&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; }</p>\n<p>}</p>\n<p>WebRequestInterceptor </p>\n<p>WebRequestInterceptor WebRequest WebRequest WebRequest Spring HttpServletRequest WebRequestInterceptor WebRequest HttpServletRequest </p>\n<p>&nbsp;</p>\n<p>1 preHandle(WebRequest request) Controller HandlerInterceptor preHandle void Hibernate Hibernate Session WebRequest setAttribute(name, value, scope) WebRequest setAttribute scope Integer WebRequest RequestAttributes </p>\n<p>&nbsp;</p>\n<p>SCOPE_REQUEST 0 request </p>\n<p>&nbsp;</p>\n<p>SCOPE_SESSION 1 sessionsessionsession</p>\n<p>&nbsp;</p>\n<p>SCOPE_GLOBAL_SESSION 2 sessionsessionsession </p>\n<p>&nbsp;</p>\n<p>2 postHandle(WebRequest request, ModelMap model) Controller ModelMap WebRequest preHandle WebRequest ModelMap Controller Model Model </p>\n<p>&nbsp;</p>\n<p>3 afterCompletion(WebRequest request, Exception ex) WebRequest preHandle Exception ControllerSpring null </p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p></p>\n<p>&nbsp;</p>\n<p>import org.springframework.ui.ModelMap;</p>\n<p>import org.springframework.web.context.request.WebRequest;</p>\n<p>import org.springframework.web.context.request.WebRequestInterceptor;</p>\n<p>public class AllInterceptor implements WebRequestInterceptor {</p>\n<p>&nbsp; &nbsp; /**&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* WebRequest&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;*/</p>\n<p>&nbsp; &nbsp; @Override</p>\n<p>&nbsp; &nbsp; public void preHandle(WebRequest request) throws Exception {</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; // TODO Auto-generated method stub&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; System.out.println(\"AllInterceptor...............................\");</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; request.setAttribute(\"request\", \"request\", WebRequest.SCOPE_REQUEST);//requestrequest&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; request.setAttribute(\"session\", \"session\", WebRequest.SCOPE_SESSION);//session&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; request.setAttribute(\"globalSession\", \"globalSession\", WebRequest.SCOPE_GLOBAL_SESSION);//&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; }</p>\n<p>&nbsp; &nbsp; /**&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* ControllerModelMapControllerModel&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* ModelMap&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;*/</p>\n<p>&nbsp; &nbsp; @Override</p>\n<p>&nbsp; &nbsp; public void postHandle(WebRequest request, ModelMap map) throws Exception {</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; // TODO Auto-generated method stub&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; for (String key:map.keySet())</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; System.out.println(key + \"-------------------------\");;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; map.put(\"name3\", \"value3\");</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; map.put(\"name1\", \"name1\");</p>\n<p>&nbsp; &nbsp; }</p>\n<p>&nbsp; &nbsp; /**&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;* &nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp;*/</p>\n<p>&nbsp; &nbsp; @Override</p>\n<p>&nbsp; &nbsp; public void afterCompletion(WebRequest request, Exception exception)</p>\n<p>&nbsp; &nbsp; throws Exception {</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; // TODO Auto-generated method stub&nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp; System.out.println(exception + \"-=-=--=--=-=-=-=-=-=-=-=-==-=--=-=-=-=\");</p>\n<p>&nbsp; &nbsp; }</p>\n<p>}</p>\n<p>&nbsp;</p>\n<p>&nbsp;SpringMVC</p>\n<p>1.SpringMVCMVCschema</p>\n<p>&nbsp;</p>\n<p>xmlns:mvc=\"http://www.springframework.org/schema/mvc\"</p>\n<p>xsi:schemaLocation=\" http://www.springframework.org/schema/mvc</p>\n<p>&nbsp; &nbsp; http://www.springframework.org/schema/mvc/spring-mvc-3.0.xsd\"</p>\n<p></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>SpringMVCmvcmvcmvc:interceptorsSpringMVC</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>mvc:interceptorsSpringMVC</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp; &nbsp;&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>mvc:interceptorspreHandlepostHandleafterCompletion</p>\n<p>&nbsp;</p>\n<p>mvc:interceptorsinterceptor</p>\n<p>&nbsp;</p>\n<p>1InterceptorbeanInterceptor</p>\n<p>&nbsp;</p>\n<p>2mvc:interceptorInterceptormvc:mapping</p>\n<p>&nbsp;</p>\n<p></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', 27, 0, 3, 0, 1, 1, '2018-11-25 20:51:52', '2017-10-06 23:54:18');
