INSERT INTO `tb_article` VALUES (26, 13, 'SpringBoot  elasticsearch ', NULL, '<p>&nbsp;<a href=\"https://liuyanzhao.com/7152.html\" target=\"_blank\" rel=\"noopener noreferrer\">mac  elasticsearch</a></p><p>&nbsp;<a href=\"https://liuyanzhao.com/tag/springboot/\" title=\" SpringBoot \" target=\"_blank\">SpringBoot</a>&nbsp; elasticsearch</p><p>&nbsp;</p><h2>  elasticsearch</h2><p><a href=\"https://liuyanzhao.com/wp-content/themes/begin/inc/go.php?url=https://www.elastic.co/downloads/past-releases\" target=\"_blank\" rel=\"noopener noreferrer\">https://www.elastic.co/downloads/past-releases</a></p><p></p><p> 2.4.4</p><p> ZIP </p><p>&nbsp;</p><p>windows  mac </p><p>windows  bin elasticsearch.bat </p><p>mac bin/elasticsearch</p><p>&nbsp;</p><p>&nbsp;</p><h2> Maven</h2><div class=\"dp-highlighter\"><div class=\"bar\"></div><ol class=\"dp-xml\" start=\"1\"><li class=\"alt\"><span class=\"comments\">&lt;!--&nbsp;Spring&nbsp;Boot&nbsp;Elasticsearch&nbsp;&nbsp;--&gt;</span></li><li class=\"\"><span class=\"tag\">&lt;</span><span class=\"tag-name\">dependency</span><span class=\"tag\">&gt;</span></li><li class=\"alt\">&nbsp;&nbsp;&nbsp;<span class=\"tag\">&lt;</span><span class=\"tag-name\">groupId</span><span class=\"tag\">&gt;</span>org.springframework.boot<span class=\"tag\">&lt;/</span><span class=\"tag-name\">groupId</span><span class=\"tag\">&gt;</span></li><li class=\"\">&nbsp;&nbsp;&nbsp;<span class=\"tag\">&lt;</span><span class=\"tag-name\">artifactId</span><span class=\"tag\">&gt;</span>spring-boot-starter-data-elasticsearch<span class=\"tag\">&lt;/</span><span class=\"tag-name\">artifactId</span><span class=\"tag\">&gt;</span></li><li class=\"alt\"><span class=\"tag\">&lt;/</span><span class=\"tag-name\">dependency</span><span class=\"tag\">&gt;</span></li></ol></div><p>&nbsp;</p><div class=\"dp-highlighter\"><div class=\"bar\"></div><ol class=\"dp-xml\" start=\"1\"><li class=\"alt\"><span class=\"tag\">&lt;</span><span class=\"tag-name\">properties</span><span class=\"tag\">&gt;</span></li><li class=\"\">&nbsp;&nbsp;&nbsp;<span class=\"tag\">&lt;</span><span class=\"tag-name\">elasticsearch.version</span><span class=\"tag\">&gt;</span>2.4.4<span class=\"tag\">&lt;/</span><span class=\"tag-name\">elasticsearch.version</span><span class=\"tag\">&gt;</span></li><li class=\"alt\"><span class=\"tag\">&lt;/</span><span class=\"tag-name\">properties</span><span class=\"tag\">&gt;</span></li></ol></div><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;spring-boot-starter-data-elasticsearch &nbsp;elasticsearch  2.4.4</p><p>&nbsp;</p><p>ES  SpirngBoot </p><p><a href=\"https://liuyanzhao.com/wp-content/themes/begin/inc/go.php?url=https://github.com/spring-projects/spring-data-elasticsearch/wiki/Spring-Data-Elasticsearch---Spring-Boot---version-matrix\" target=\"_blank\" rel=\"noopener noreferrer\">https://github.com/spring-projects/spring-data-elasticsearch/wiki/Spring-Data-Elasticsearch---Spring-Boot---version-matrix</a></p><p>&nbsp;</p><h2> application.properties</h2><div class=\"dp-highlighter\"><div class=\"bar\"></div><ol class=\"dp-xml\" start=\"1\"><li class=\"alt\">#Project</li><li class=\"\"><span class=\"attribute\">server.port</span>=<span class=\"attribute-value\">8080</span></li><li class=\"alt\"><span class=\"attribute\">debug</span>=<span class=\"attribute-value\">true</span></li><li class=\"\"><span class=\"attribute\">server.context-path</span>=/chuyun</li><li class=\"alt\"></li><li class=\"\"></li><li class=\"alt\">#&nbsp;DataSource</li><li class=\"\"><span class=\"attribute\">spring.datasource.driver-class-name</span>=<span class=\"attribute-value\">com</span>.mysql.jdbc.Driver</li><li class=\"alt\"><span class=\"attribute\">spring.datasource.username</span>=<span class=\"attribute-value\">root</span></li><li class=\"\"><span class=\"attribute\">spring.datasource.password</span>=<span class=\"attribute-value\">123456</span></li><li class=\"alt\"><span class=\"attribute\">spring.datasource.url</span>=<span class=\"attribute-value\