alt\"><span class=\"comment\">&nbsp;*&nbsp;@date&nbsp;2018/1/22&nbsp;9:07</span></li><li class=\"\"><span class=\"comment\">&nbsp;*/</span></li><li class=\"alt\"></li><li class=\"\"></li><li class=\"alt\"><span class=\"annotation\">@RestController</span></li><li class=\"\"><span class=\"annotation\">@RequestMapping</span>(<span class=\"string\">\"/article\"</span>)</li><li class=\"alt\"><span class=\"keyword\">public</span>&nbsp;<span class=\"keyword\">class</span>&nbsp;ArticleController&nbsp;{</li><li class=\"\"></li><li class=\"alt\">&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@Autowired</span></li><li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">private</span>&nbsp;ArticleRepository&nbsp;ArticleRepository;</li><li class=\"alt\"></li><li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@RequestMapping</span>(<span class=\"string\">\"\"</span>)</li><li class=\"alt\">&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">public</span>&nbsp;List&lt;Article&gt;&nbsp;list(<span class=\"annotation\">@RequestParam</span>(value&nbsp;=&nbsp;<span class=\"string\">\"title\"</span>,&nbsp;required&nbsp;=&nbsp;<span class=\"keyword\">false</span>)&nbsp;String&nbsp;title,</li><li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@RequestParam</span>(value&nbsp;=&nbsp;<span class=\"string\">\"content\"</span>,&nbsp;required&nbsp;=&nbsp;<span class=\"keyword\">false</span>)&nbsp;String&nbsp;content,</li><li class=\"alt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@RequestParam</span>(value&nbsp;=&nbsp;<span class=\"string\">\"pageIndex\"</span>,&nbsp;defaultValue&nbsp;=&nbsp;<span class=\"string\">\"0\"</span>)&nbsp;<span class=\"keyword\">int</span>&nbsp;pageIndex,</li><li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"annotation\">@RequestParam</span>(value&nbsp;=&nbsp;<span class=\"string\">\"pageSize\"</span>,&nbsp;defaultValue&nbsp;=&nbsp;<span class=\"string\">\"10\"</span>)&nbsp;<span class=\"keyword\">int</span>&nbsp;pageSize)&nbsp;{</li><li class=\"alt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pageable&nbsp;pageable&nbsp;=&nbsp;<span class=\"keyword\">new</span>&nbsp;PageRequest(pageIndex,&nbsp;pageSize);</li><li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Page&lt;Article&gt;&nbsp;page&nbsp;=&nbsp;ArticleRepository.findDistinctByTitleContainingOrContentContaining(title,&nbsp;content,&nbsp;pageable);</li><li class=\"alt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"keyword\">return</span>&nbsp;page.getContent();</li><li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;}</li><li class=\"alt\">}</li></ol></div><p>&nbsp;</p><p>   elasticsearch </p><p></p><p>http://localhost:8080/chuyun/article?title=&amp;content=</p><p><img class=\"wp-image-7181 aligncenter\" data-original=\"https://media.liuyanzhao.com/wp-content/uploads/2018/01/es11-1024x372.jpg\" src=\"https://media.liuyanzhao.com/wp-content/uploads/2018/01/es11-1024x372.jpg\" alt=\"SpringBoot  elasticsearch \" width=\"646\" height=\"235\" srcset=\"https://media.liuyanzhao.com/wp-content/uploads/2018/01/es11-1024x372.jpg 1024w, https://media.liuyanzhao.com/wp-content/uploads/2018/01/es11-300x109.jpg 300w, https://media.liuyanzhao.com/wp-content/uploads/2018/01/es11-768x279.jpg 768w\" sizes=\"(max-width: 646px) 100vw, 646px\"></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p><a href=\"https://liuyanzhao.com/7170.html\" target=\"_blank\" rel=\"noopener noreferrer\">https://liuyanzhao.com/7170.html</a></p>', 1, 0, 0, 1, 