INSERT INTO `tb_article` VALUES (30, 1, 'SpringBoot + mongodb , ', 'mongodb  Redis Redis ', '<div class=\"single-content\">\n<p>mongodb  Redis Redis </p>\n<p> mongodb ( MySQL) mongodb </p>\n<p>&nbsp;<a title=\" SpringBoot \" href=\"https://liuyanzhao.com/tag/springboot/\" target=\"_blank\" rel=\"noopener\">SpringBoot</a>&nbsp; mongodb </p>\n<p>&nbsp;</p>\n<h2></h2>\n<p> springboot  web  lombok mongodb </p>\n<p><strong>1pom.xml</strong></p>\n<div class=\"dp-highlighter\">\n<div class=\"bar\">&nbsp;</div>\n<ol class=\"dp-xml\" start=\"1\">\n<li class=\"alt\"><span class=\"comments\"><!--mongodb--></span></li>\n<li class=\"\"><span class=\"tag\">&lt;</span><span class=\"tag-name\">dependency</span><span class=\"tag\">&gt;</span></li>\n<li class=\"alt\">&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"tag\">&lt;</span><span class=\"tag-name\">groupId</span><span class=\"tag\">&gt;</span>org.springframework.boot<span class=\"tag\"><!--</span--><span class=\"tag-name\">groupId</span><span class=\"tag\">&gt;</span></span></li>\n<li class=\"\">&nbsp;&nbsp;&nbsp;&nbsp;<span class=\"tag\">&lt;</span><span class=\"tag-name\">artifactId</span><span class=\"tag\">&gt;</span>spring-boot-starter-data-mongodb<span class=\"tag\"><!--</span--><span class=\"tag-name\">artifactId</span><span class=\"tag\">&gt;</span></span></li>\n<li class=\"alt\"><span class=\"tag\"><!--</span--><span class=\"tag-name\">dependency</span><span class=\"tag\">&gt;</span></span></li>\n</ol>\n</div>\n<p> Spring Data JPA</p>\n<p>&nbsp;</p>\n<p><strong>2application.properties</strong></p>\n<div class=\"dp-highlighter\">\n<div class=\"bar\">&nbsp;</div>\n<ol class=\"dp-j\" start=\"1\">\n<li class=\"alt\">spring.data.mongodb.uri=mongodb:<span class=\"comment\">//localhost:27017/saysky</span></li>\n</ol>\n</div>\n<p>saysky mongodb</p>\n<p>&nbsp;</p>\n<h2></h2>\n<p><strong>1</strong></p>\n<div class=\"dp-highlighter\">\n<div class=\"bar\">&nbsp;</div>\n<ol class=\"dp-j\" start=\"1\">\n<li class=\"alt\"><span class=\"keywor