INSERT INTO `tb_article` VALUES (33, 1, 'MySQL', ' select * from bbs where id=1; insert into bbs (name,data_year) values (\"jack\",\"1993-10-01\"); update bbs set name=\"tom\",sex=1,age=18 where ', '<p><strong></strong></p><p> select * from bbs where id=1;</p><p> insert into bbs (name,data_year) values (\"jack\",\"1993-10-01\");</p><p> update bbs set name=\"tom\",sex=1,age=18 where name=\"jack\";</p><p> delete form bbs where id=2;</p><p>&nbsp;UPDATE `category` SET guid = REPLACE ( guid, \'articles\', \'article\' );</p><p>&nbsp;</p><p><strong></strong></p><p>1.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"wp_keywordlink_affiliate\">mysql</span>&nbsp;-u&nbsp;root&nbsp;-p</li><li><span class=\"wp_keywordlink_affiliate\">mysql</span>&nbsp;-h&nbsp;localhost&nbsp;-u&nbsp;root&nbsp;-p&nbsp;database_name</li></ol></div><p>2.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"wp_keywordlink_affiliate\">show</span>&nbsp;databases;</li></ol></div><p>3.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">use&nbsp;databases_name;</li></ol></div><p>4.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"wp_keywordlink_affiliate\">show</span>&nbsp;tables;</li></ol></div><p>5.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"wp_keywordlink_affiliate\">show</span>&nbsp;columns&nbsp;<span class=\"keyword\">from</span>&nbsp;table_name;</li><li>describe&nbsp;table_name;</li></ol></div><p>6.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"wp_keywordlink_affiliate\">mysql</span>dump&nbsp;-u&nbsp;user_name&nbsp;-p&nbsp;database_name&nbsp;&gt;&nbsp;/tmp/file_name</li></ol></div><p>my<span class=\"wp_keywordlink_affiliate\">sql</span>dump -u root -p test_db &gt; d:/test_db.<span class=\"wp_keywordlink_affiliate\">sql</span></p><p>7.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">my<span class=\"wp_keywordlink_affiliate\">sql</span>dump&nbsp;-u&nbsp;user_name&nbsp;-p&nbsp;database_name&nbsp;table_name&nbsp;&gt;&nbsp;/tmp/file_name</li></ol></div><p>mysqldump -u root -p test_db table1 &gt; d:/table1.sql</p><p>8.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">mysqldump&nbsp;-u&nbsp;user_name&nbsp;-p&nbsp;-d&nbsp;<span class=\"comment\">--add--table&nbsp;database_name&nbsp;&gt;&nbsp;file_name</span></li></ol></div><p>mysqldump -u root -p -d --add-<span class=\"wp_keywordlink_affiliate\">drop</span>-table test_db &gt; test_db.sql</p><p>9.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">source&nbsp;file_name;</li><li></li><li class=\"alt\">mysql&nbsp;-u&nbsp;user_name&nbsp;-p&nbsp;database_name&nbsp;&lt;&nbsp;file_name</li></ol></div><p></p><p>source /tmp/bbs.sql</p><p>source d:/bbs.sql</p><p>mysql -u root -p bbs &lt; \"d:/bbs.sql\"</p><p>mysql -u root -p bbs &lt; \"/tmp/bbs.sql\"</p><p>10.excel</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">load</span>&nbsp;data&nbsp;infile&nbsp;<span class=\"string\">\"tables.txt\"</span>&nbsp;<span class=\"keyword\">into</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name;</li></ol></div><p></p><p>load data infile \"/tmp/bbs.txt\" into table bbs</p><p>load data infile \"/tmp/bbs.xls\" into table bbs</p><p>load data infile \"d:/bbs.txt\" into table bbs</p><p>load data infile \"d:/bbs.xls\" into table bbs</p><p>11.excel</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\"><span class=\"wp_keywordlink_affiliate\">select</span></span>&nbsp;*&nbsp;<span class=\"keyword\">into</span>&nbsp;outfile&nbsp;<span class=\"string\">\"path_file_name\"</span>&nbsp;<span class=\"keyword\">from</span>&nbsp;table_name;</li></ol></div><p></p><p><span class=\"wp_keywordlink_affiliate\">select</span>&nbsp;* into outfile \"/tmp/bbs.txt\" from bbs</p><p>select * into outfile \"/tmp/bbs.xls\" from bbs where id=1;</p><p>select * into outfile \"d:/bbs.txt\" from bbs;</p><p>select * into outfile \"d:/bbs.xls\" from bbs where id=1;</p><p>12.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">database</span>&nbsp;if&nbsp;<span class=\"op\">not</span>&nbsp;exists&nbsp;database_name;</li></ol></div><p>create database if not exists bbs</p><p>13.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">database</span>&nbsp;database_name;</li></ol></div><p>create database bbs;</p><p>14.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">drop</span>&nbsp;<span class=\"keyword\">database</span>&nbsp;database_name;</li></ol></div><p>drop database bbs;</p><p>15.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">mysql&gt;&nbsp;<span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;<table_name>&nbsp;(&nbsp;&lt;<span class=\"keyword\">column</span>&nbsp;1&nbsp;<span class=\"keyword\">name</span>&gt;&nbsp;<col.1type>&nbsp;<col.1details>,&lt;<span class=\"keyword\">column</span>&nbsp;2&nbsp;<span class=\"keyword\">name</span>&gt;&nbsp;<col.2type>&nbsp;<col.2details>,&nbsp;...);</col.2details></col.2type></col.1details></col.1type></table_name></li></ol></div><p>create table (id int not null auto_increment primary key,name char(16) not null default \"jack\",date_year date not null);</p><p><span>16.</span></p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">delete</span>&nbsp;<span class=\"keyword\">from</span>&nbsp;table_name;</li></ol></div><p></p><p>delete from bbs;</p><p>delete from bbs where id=2;</p><p>17.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">drop</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name;</li></ol></div><p></p><p>drop table test_db;</p><p>rm -f database_name/table_name.* (linux</p><p></p><p>rm -rf bbs/accp.*</p><p><span>18.</span></p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\"><span class=\"wp_keywordlink_affiliate\">insert</span></span>&nbsp;<span class=\"keyword\">into</span>&nbsp;table_name&nbsp;<span class=\"keyword\">set</span>&nbsp;column_name1=value1,column_name2=value2;</li></ol></div><p><span class=\"wp_keywordlink_affiliate\">insert</span>&nbsp;into bbs set name=\"jack\",date_year=\"1993-10-01\";</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\"><span class=\"wp_keywordlink_affiliate\">insert</span></span>&nbsp;<span class=\"keyword\">into</span>&nbsp;table_name&nbsp;<span class=\"keyword\">values</span>&nbsp;(column1,column2,...);</li></ol></div><p>insert into bbs (\"2\",\"jack\",\"1993-10-02\")</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">insert</span>&nbsp;<span class=\"keyword\">into</span>&nbsp;table_name&nbsp;(column_name1,column_name2,...)&nbsp;<span class=\"keyword\">values</span>&nbsp;(value1,value2);</li></ol></div><p>insert into bbs (name,data_year) values (\"jack\",\"1993-10-01\");</p><p><span>19.</span></p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">select</span>&nbsp;*&nbsp;<span class=\"keyword\">from</span>&nbsp;table_name;</li></ol></div><p>select * from bbs where id=1;</p><p><span>20.</span></p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">update</span>&nbsp;table_name&nbsp;<span class=\"keyword\">set</span>&nbsp;col_name=new_value&nbsp;<span class=\"keyword\">where</span>&nbsp;id=1;</li></ol></div><p>update bbs set name=\"tom\",age=18 where name=\"jack\";</p><p>21.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;<span class=\"keyword\">add</span>&nbsp;<span class=\"keyword\">column</span>&nbsp;field_name&nbsp;datatype&nbsp;<span class=\"op\">not</span>&nbsp;<span class=\"op\">null</span>&nbsp;<span class=\"keyword\">default</span>&nbsp;<span class=\"string\">\"1\"</span>;</li></ol></div><p>alter table bbs add column tel char(16) not null;</p><p>22.(column</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;<span class=\"keyword\">add</span>&nbsp;<span class=\"keyword\">column</span>&nbsp;filed_name1&nbsp;datatype,<span class=\"keyword\">add</span>&nbsp;<span class=\"keyword\">column</span>&nbsp;filed_name2&nbsp;datatype;</li></ol></div><p>alter table bbs add column tel char(16) not null,add column address text;</p><p>23.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;<span class=\"keyword\">drop</span>&nbsp;field_name;</li></ol></div><p>alter table bbs drop tel;</p><p>24.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;<span class=\"keyword\">modify</span>&nbsp;id&nbsp;<span class=\"keyword\">int</span>&nbsp;unsigned;//id<span class=\"keyword\">int</span>&nbsp;unsigned</li><li><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;change&nbsp;id&nbsp;sid&nbsp;<span class=\"keyword\">int</span>&nbsp;unsigned;//idsid<span class=\"keyword\">int</span>&nbsp;unsigned</li></ol></div><p>25.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;<span class=\"keyword\">modify</span>&nbsp;column_name&nbsp;datatype&nbsp;<span class=\"op\">not</span>&nbsp;<span class=\"op\">null</span>&nbsp;<span class=\"keyword\">default</span>&nbsp;<span class=\"string\">\"\"</span>;</li></ol></div><p>alter table test_db modify name char(16) default not null \"yourname\";</p><p>26.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table_name&nbsp;rename&nbsp;<span class=\"keyword\">as</span>&nbsp;new_table_name;</li></ol></div><p>alter table bbs rename as bbs_table;</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">rename&nbsp;<span class=\"keyword\">table</span>&nbsp;old_table_name&nbsp;<span class=\"keyword\">to</span>&nbsp;new_table_name;</li></ol></div><p>rename table test_db to accp;</p><p>27.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table2&nbsp;<span class=\"keyword\">select</span>&nbsp;*&nbsp;<span class=\"keyword\">from</span>&nbsp;table1&nbsp;<span class=\"keyword\">where</span>&nbsp;1&lt;&gt;1;</li></ol></div><p>create table test_db select * from accp where 1&lt;&gt;1;</p><p>28.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">select</span>&nbsp;now();</li></ol></div><p>29.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">select</span>&nbsp;<span class=\"func\">user</span>();</li></ol></div><p>30.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">select</span>&nbsp;version();</li></ol></div><p>31.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table1&nbsp;<span class=\"keyword\">add</span>&nbsp;<span class=\"keyword\">index</span>&nbsp;ind_id(id);</li><li><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">index</span>&nbsp;ind_id&nbsp;<span class=\"keyword\">on</span>&nbsp;table1(id);</li><li class=\"alt\"><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">unique</span>&nbsp;<span class=\"keyword\">index</span>&nbsp;ind_id&nbsp;<span class=\"keyword\">on</span>&nbsp;table1(id);//</li></ol></div><p>32.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">drop</span>&nbsp;<span class=\"keyword\">index</span>&nbsp;idx_id&nbsp;<span class=\"keyword\">on</span>&nbsp;table1;</li><li><span class=\"keyword\">alter</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table1&nbsp;<span class=\"keyword\">drop</span>&nbsp;<span class=\"keyword\">index</span>&nbsp;ind_id;</li></ol></div><p>33.id\":\"name\"=\"</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">select</span>&nbsp;concat(id\':\',<span class=\"keyword\">name</span>,\'=\')&nbsp;<span class=\"keyword\">from</span>&nbsp;<span class=\"keyword\">table</span>;</li></ol></div><p>34.limit1020</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">select</span>&nbsp;*&nbsp;<span class=\"keyword\">from</span>&nbsp;bbs&nbsp;<span class=\"keyword\">order</span>&nbsp;<span class=\"keyword\">by</span>&nbsp;id&nbsp;limit&nbsp;9,10;</li></ol></div><p></p><p>35.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\"><span class=\"wp_keywordlink_affiliate\">grant</span></span>&nbsp;<span class=\"op\">all</span>&nbsp;<span class=\"keyword\">on</span>&nbsp;*.*&nbsp;<span class=\"keyword\">to</span>&nbsp;<span class=\"func\">user</span>@localhost&nbsp;identified&nbsp;<span class=\"keyword\">by</span>&nbsp;<span class=\"string\">\"password\"</span>;</li></ol></div><p>36.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;if&nbsp;<span class=\"op\">not</span>&nbsp;exists&nbsp;students();</li></ol></div><p>37.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\">create</span>&nbsp;<span class=\"keyword\">table</span>&nbsp;table2&nbsp;<span class=\"keyword\">select</span>&nbsp;*&nbsp;<span class=\"keyword\">from</span>&nbsp;table1;</li></ol></div><p>create table test_db select * from accp;</p><p>38.mysql</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\"><span class=\"keyword\"><span class=\"wp_keywordlink_affiliate\">grant</span></span>&nbsp;<span class=\"op\">all</span>&nbsp;<span class=\"keyword\">privileges</span>&nbsp;<span class=\"keyword\">on</span>&nbsp;*.*&nbsp;<span class=\"keyword\">to</span>&nbsp;<span class=\"string\">\"root\"</span>@<span class=\"string\">\"%\"</span>&nbsp;identified&nbsp;<span class=\"keyword\">by</span>&nbsp;<span class=\"string\">\"password\"</span>&nbsp;<span class=\"keyword\">with</span>&nbsp;<span class=\"keyword\">grant</span>&nbsp;<span class=\"keyword\">option</span>;</li></ol></div><p>mysqluserhost</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">use&nbsp;mysql;</li><li><span class=\"keyword\">select</span>&nbsp;<span class=\"func\">user</span>,host&nbsp;<span class=\"keyword\">from</span>&nbsp;<span class=\"func\">user</span>;</li><li class=\"alt\"><span class=\"keyword\">update</span>&nbsp;<span class=\"func\">user</span>&nbsp;<span class=\"keyword\">set</span>&nbsp;host=<span class=\"string\">\"%\"</span>&nbsp;<span class=\"keyword\">where</span>&nbsp;<span class=\"func\">user</span>=<span class=\"string\">\"user_name\"</span>;</li></ol></div><p>39.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">show&nbsp;status;</li></ol></div><p>40.</p><div class=\"dp-highlighter\"><ol class=\"dp-sql\"><li class=\"alt\">show&nbsp;processlist;</li></ol></div><p>rootshow status; Threads_connected</p><p><a href=\"https://li